import { useState, useEffect } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import { Button, Divider, Typography, Card, Box, Avatar } from "@mui/material";
import useAuth from "./hooks/useAuth";

const Profile = () => {
  const [profile, setProfile] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  //const { state } = useLocation();
  const { login } = useParams();
  const { auth } = useAuth();
  const [loginProf, setLoginProf] = useState();

  if (!login) {
    //setLoginProf(auth.login);
    setLoginProf("flafyx");
  }

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    //console.log("---------LOGIN PROF", loginProf);
    const getUsersProfile = async () => {
      try {
        const response = await axiosPrivate.get("/profile", {
          signal: controller.signal,
          params: { login: login },
        });
        isMounted && setProfile(response.data.rows[0]);
        console.log("response", response.data.rows[0]);
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersProfile();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        height: "100%",
      }}
    >
      {profile ? (
        <Card
          sx={{
            width: 350,
            height: 500,
            m: 2,
            display: "flex",
            flexDirection: "row",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {" "}
          <Avatar>{profile.login[0].toUpperCase()}</Avatar>
          <Typography sx={{ fontWeight: 600 }}>
            {profile.login.toUpperCase()}
          </Typography>
          <Typography>{profile.first_name}</Typography>
          <Typography>{profile.last_name}</Typography>
          <Typography>{profile.email}</Typography>
          <Typography>{profile.role}</Typography>
        </Card>
      ) : (
        <p>Brak profilu do wy≈õwietlenia</p>
      )}
    </Box>
  );
};

export default Profile;
