import {
  Box,
  Button,
  Card,
  Divider,
  TextField,
  Typography,
} from "@mui/material";
import { DatePicker } from "@mui/x-date-pickers";
import React from "react";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import {
  plPL,
  LocalizationProvider,
  DateTimePicker,
} from "@mui/x-date-pickers";
import "dayjs/locale/pl";
import { useState } from "react";
import dayjs from "dayjs";
import AddIcon from "@mui/icons-material/Add";

const HomeWorkCreator = () => {
  const [testDate, setTestDate] = useState();
  const handleChange = (newValue) => {
    setTestDate(newValue);
  };

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <Typography
        variant="h6"
        sx={{ fontWeight: 600, color: "rgba(0, 0, 0, 0.6)", mt: 4, mb: 3 }}
      >
        Stwórz zadanie domowe
      </Typography>{" "}
      <Divider sx={{ width: "100%", mb: 4 }}></Divider>
      <Card
        sx={{
          width: 400,
          height: "70vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 4,
        }}
      >
        <Typography
          sx={{ fontWeight: 500, color: "rgba(0, 0, 0, 0.6)", mt: 4, mb: 3 }}
        >
          Zadanie domowe
        </Typography>
        <TextField sx={{ width: "80%" }} label="Tytuł"></TextField>
        <TextField
          label="Treść"
          multiline
          rows={4}
          sx={{ width: "80%" }}
        ></TextField>
        <LocalizationProvider
          dateAdapter={AdapterDayjs}
          adapterLocale="pl"
          localeText={
            plPL.components.MuiLocalizationProvider.defaultProps.localeText
          }
        >
          <DateTimePicker
            renderInput={(params) => (
              <TextField sx={{ width: "80%" }} {...params} />
            )}
            label="Data i czas testu"
            value={testDate}
            onChange={handleChange}
            minDateTime={dayjs()}
          />
        </LocalizationProvider>
        <Button
          sx={{ mb: 2, mt: "auto", mr: 2, ml: "auto" }}
          variant="contained"
          startIcon={<AddIcon></AddIcon>}
        >
          Dodaj
        </Button>
      </Card>
    </Box>
  );
};

export default HomeWorkCreator;
