import { useState, useEffect, useStyles } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import useAuth from "./hooks/useAuth";

function createData(obj) {
  const description = obj.description;
  const answer_1 = obj.answer_1;
  const answer_2 = obj.answer_2;
  const answer_3 = obj.answer_3;
  const answer_4 = obj.answer_4;
  return { description, answer_1, answer_2, answer_3, answer_4 };
}

const TestPage = () => {
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const login = window.localStorage.getItem("login");
  const testId = window.location.pathname.replace("/test/", "");
  const [test, setTest] = useState();
  const [testName, setTestName] = useState();

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getTest = async () => {
      try {
        const response = await axiosPrivate.get("/test/" + testId, {
          signal: controller.signal,
          params: { login: login, test: testId },
        });
        console.log(response);
        isMounted && setTest(response.data.test.rows);
        setTestName(response.data.testName.rows[0].name);

        setTest(
          response.data.test.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getTest();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  console.log(test);
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {testName ? (
        <>
          <Typography variant="h5">{testName}</Typography>
          <Divider sx={{ width: "100%", mb: 2, mt: 2 }}></Divider>
          <TableContainer component={Paper} sx={{ maxWidth: 700 }}>
            <Table sx={{ Width: 550 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell align="left">ID</TableCell>
                  <TableCell align="center">Opis</TableCell>
                  <TableCell align="center">Odpowiedz 1</TableCell>
                  <TableCell align="center">Odpowiedz 2</TableCell>
                  <TableCell align="center">Odpowiedz 3</TableCell>
                  <TableCell align="center">Odpowiedz 4</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {test.map((row, i) => (
                  <TableRow
                    className="groupTableHover"
                    key={row.counter}
                    sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                  >
                    <TableCell align="left">{i + 1}</TableCell>
                    <TableCell align="center">{row.description}</TableCell>
                    <TableCell align="center">{row.answer_1}</TableCell>
                    <TableCell align="center">{row.answer_2}</TableCell>
                    <TableCell align="center">{row.answer_3}</TableCell>
                    <TableCell align="center">{row.answer_4}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </>
      ) : (
        <p>Brak testu do wy≈õwietlenia</p>
      )}
    </Box>
  );
};

export default TestPage;
