import React from "react";
import {
  Box,
  Button,
  Card,
  Divider,
  TextField,
  Typography,
} from "@mui/material";
import { useEffect, useRef } from "react";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import {
  plPL,
  LocalizationProvider,
  DateTimePicker,
} from "@mui/x-date-pickers";
import "dayjs/locale/pl";
import { useState } from "react";
import dayjs from "dayjs";
import AddIcon from "@mui/icons-material/Add";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useLocation, useNavigate } from "react-router-dom";
import useAlert from "./hooks/useAlert";

const HomeworkPage = () => {
  const [file, setFile] = useState();
  const homeworkId = useRef();
  const { setAlert, setAlertMessage, setAlertType } = useAlert();

  homeworkId.current = window.location.pathname.replace("/homework/solve/", "");

  const handleSubmit = async (event) => {
    event.preventDefault();

    let homeworkIdCurrent = homeworkId.current;

    await axiosPrivate
      .post("/homework/", JSON.stringify({ homeworkIdCurrent, login }), {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      })
      .then((response) => {
        console.log("Halo", response);
        setAlert(true);
        setAlertType("success");
        setAlertMessage("Dodano zadanie do grupy!");
        navigate("/dashboard");
      })
      .catch((error) => {
        //Error handling
        if (error.response) {
          //The client was given an error response

          if (!error.response.data.type) {
            if (error.response.status >= 400) {
              console.log("error");
            } else if (error.response.status >= 200) {
              console.log("error");
            }
          } else {
            console.log("error");
          }
          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          console.log(error.request);
        } else {
          //Other errors
          console.log("Error", error.message);
        }
      });
  };

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getGroup = async () => {
      try {
        const response = await axiosPrivate.get(
          "/homework/solve" + homeworkId,
          {
            signal: controller.signal,
            params: { login: login, homeworkId: homeworkId },
          }
        );
        console.log(response);
        isMounted && setRows(response.data.group.rows);
        setGroupName(response.data.groupName.rows[0].name);

        setRows(
          response.data.group.rows.map((row) => {
            return createData(row);
          })
        );
        // let arrayTemp = [];
        // response.data.grades.rows.map((row) => {
        //   if (row.score != null) {
        //     arrayTemp.push(row);
        //   }
        // });

        setGrades(response.data.grades.rows);
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getGroup();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <Typography
        variant="h6"
        sx={{ fontWeight: 600, color: "rgba(0, 0, 0, 0.6)", mt: 4, mb: 3 }}
      >
        Stwórz zadanie domowe
      </Typography>{" "}
      <Divider sx={{ width: "100%", mb: 4 }}></Divider>
      <Card
        sx={{
          width: 400,
          height: "70vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 4,
        }}
      >
        <Typography
          sx={{ fontWeight: 500, color: "rgba(0, 0, 0, 0.6)", mt: 4, mb: 3 }}
        >
          Zadanie domowe
        </Typography>

        <Button
          sx={{ mb: 2, mt: "auto", mr: 2, ml: "auto" }}
          variant="contained"
          startIcon={<AddIcon></AddIcon>}
          onClick={handleSubmit}
        >
          Wyślij zadanie
        </Button>
      </Card>
    </Box>
  );
};

export default HomeworkPage;
