import { Button, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useState, useEffect } from "react";
import SendMailPopUp from "./SendMailPopUp";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useLocation, useNavigate } from "react-router-dom";

function createData(obj) {
  const sender_id = obj.sender_id;
  const receiver_id = obj.receiver_id;
  const text = obj.text;
  const title = obj.title;
  const timestamp = obj.timestamp;
  return { sender_id, receiver_id, text, title, timestamp };
}

const MailPage = () => {
  const [open, setOpen] = useState(false);
  const login = window.localStorage.getItem("login");
  const [rows, setRows] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();

  const handlePopUp = () => {
    setOpen(!open);
  };

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getMail = async () => {
      try {
        const response = await axiosPrivate.get("/mail/" + login, {
          signal: controller.signal,
          params: { login: login },
        });
        console.log(response);
        isMounted && setRows(response.data.mail.rows);

        setRows(
          response.data.mail.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getMail();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box>
      {open ? (
        <SendMailPopUp handlePopUp={handlePopUp} open={open}></SendMailPopUp>
      ) : (
        <></>
      )}
      <Typography>Skrzynka Pocztowa</Typography>
      <Button onClick={handlePopUp}>Wyślij wiadomość</Button>
    </Box>
  );
};

export default MailPage;
