import { useState, useEffect, useRef } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Checkbox,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Tooltip,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AddStudentPopUp from "./AddStudentPopUp";
import GroupTests from "./GroupTests";
import SendIcon from "@mui/icons-material/Send";
import StudentsTable from "./StudentsTable";

function createData(obj) {
  const students_id = obj.students_id;
  const first_name = obj.first_name;
  const last_name = obj.last_name;
  const ownerLogin = obj.login;
  return { students_id, first_name, last_name, ownerLogin };
}

const GroupsBoardPage = () => {
  const [rows, setRows] = useState();
  const [groupName, setGroupName] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const login = window.localStorage.getItem("login");
  const [open, setOpen] = useState(false);
  const groupId = useRef();

  groupId.current = window.location.pathname.replace("/groups/", "");

  const handleOpenAdd = () => {
    setOpen(true);
  };

  const handleDeleteStudent = async (studentId) => {
    console.log(rows);
    setRows(rows.filter((row) => row.students_id !== studentId));
    let groupIdCurrent = groupId.current;
    await axiosPrivate
      .delete(
        "/students/" + studentId + "/" + groupIdCurrent,
        JSON.stringify({ groupIdCurrent, studentId }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
          params: { studentId: studentId, groupId: groupIdCurrent },
        }
      )
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        //Error handling
        if (error.response) {
          //The client was given an error response

          if (!error.response.data.type) {
            if (error.response.status >= 400) {
              //setAlertType("error");
              console.log("error");
            } else if (error.response.status >= 200) {
              console.log("error");
            }
          } else {
            console.log("error");
          }

          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
        } else if (error.request) {
          //The client never received a response
          console.log(error.request);
        } else {
          //Other errors
          console.log("Error", error.message);
        }
      });
  };

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    let groupIdParam = groupId.current;
    const getGroup = async () => {
      try {
        const response = await axiosPrivate.get("/group/" + groupIdParam, {
          signal: controller.signal,
          params: { login: login, group: groupIdParam },
        });
        console.log(response);
        isMounted && setRows(response.data.group.rows);
        setGroupName(response.data.groupName.rows[0].name);

        setRows(
          response.data.group.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getGroup();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {open ? (
        <AddStudentPopUp
          open={open}
          setOpen={setOpen}
          groupId={groupId}
        ></AddStudentPopUp>
      ) : (
        <></>
      )}
      {groupName ? (
        <>
          <Typography sx={{ color: "rgba(0, 0, 0, 0.6)" }} variant="h5">
            {groupName}
          </Typography>
          <Divider sx={{ width: "100%", mb: 2, mt: 2 }}></Divider>
          <Button onClick={() => handleOpenAdd()}>Dodaj ucznia</Button>
          <TableContainer component={Paper} sx={{ maxWidth: 450 }}>
            <Table sx={{ Width: 350 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell align="left">ID</TableCell>
                  <TableCell align="center">Imie</TableCell>
                  <TableCell align="center">Nazwisko</TableCell>
                  <TableCell align="center" className="shortTableCell">
                    Akcje
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row, i) => (
                  <TableRow
                    className="groupTableHover"
                    key={row.counter}
                    sx={{
                      "&:last-child td, &:last-child th": { border: 0 },
                    }}
                  >
                    <TableCell align="left">{i + 1}</TableCell>
                    <TableCell align="center">{row.first_name}</TableCell>
                    <TableCell align="center">{row.last_name}</TableCell>
                    <TableCell className="shortTableCell">
                      {row.ownerLogin == login ? (
                        <>
                          <Tooltip title="Usuń">
                            <IconButton
                              onClick={() =>
                                handleDeleteStudent(row.students_id)
                              }
                            >
                              <DeleteIcon fontSize="medium" color="error" />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Napisz">
                            <IconButton>
                              <SendIcon fontSize="medium" />
                            </IconButton>
                          </Tooltip>
                        </>
                      ) : (
                        <></>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </>
      ) : (
        <p>Brak grupy do wyświetlenia</p>
      )}
      <GroupTests groupId={groupId}></GroupTests>
      {rows ? (
        <StudentsTable groupId={groupId} rows={rows}></StudentsTable>
      ) : (
        <></>
      )}
    </Box>
  );
};

export default GroupsBoardPage;
