import { Box, Card, Divider, TextField, Typography } from "@mui/material";
import { DatePicker } from "@mui/x-date-pickers";
import React from "react";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import {
  plPL,
  LocalizationProvider,
  DateTimePicker,
} from "@mui/x-date-pickers";
import "dayjs/locale/pl";
import { useState } from "react";
import dayjs from "dayjs";

const HomeWorkCreator = () => {
  const [testDate, setTestDate] = useState();
  const handleChange = (newValue) => {
    setTestDate(newValue);
  };

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <Typography
        variant="h6"
        sx={{ fontWeight: 600, color: "rgba(0, 0, 0, 0.6)", mt: 4, mb: 3 }}
      >
        Stw√≥rz zadanie domowe
      </Typography>{" "}
      <Divider sx={{ width: "100%", mb: 4 }}></Divider>
      <Card
        sx={{
          width: 400,
          height: "70vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          gap: 2,
        }}
      >
        <TextField variant="standard"></TextField>
        <TextField variant="standard"></TextField>
        <LocalizationProvider
          dateAdapter={AdapterDayjs}
          adapterLocale="pl"
          localeText={
            plPL.components.MuiLocalizationProvider.defaultProps.localeText
          }
        >
          <DateTimePicker
            renderInput={(params) => <TextField {...params} />}
            label="Data i czas testu"
            value={testDate}
            onChange={handleChange}
            minDateTime={dayjs()}
          />
        </LocalizationProvider>
      </Card>
    </Box>
  );
};

export default HomeWorkCreator;
