import { useState, useEffect, useStyles } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  RadioGroup,
  FormControlLabel,
  Radio,
  TextField,
} from "@mui/material";
import useAuth from "./hooks/useAuth";

function createData(obj) {
  const description = obj.description;
  const answer_1 = obj.answer_1;
  const answer_2 = obj.answer_2;
  const answer_3 = obj.answer_3;
  const answer_4 = obj.answer_4;
  return { description, answer_1, answer_2, answer_3, answer_4 };
}

const TestPage = () => {
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const login = window.localStorage.getItem("login");
  const testId = window.location.pathname.replace("/test/", "");
  const [test, setTest] = useState();
  const [testName, setTestName] = useState();

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getTest = async () => {
      try {
        const response = await axiosPrivate.get("/test/" + testId, {
          signal: controller.signal,
          params: { login: login, test: testId },
        });
        console.log(response);
        isMounted && setTest(response.data.test.rows);
        setTestName(response.data.testName.rows[0].name);

        setTest(
          response.data.test.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getTest();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  console.log(test);
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {testName ? (
        <>
          <Typography variant="h5">{testName}</Typography>
          <Divider sx={{ width: "100%", mb: 2, mt: 2 }}></Divider>
          <Paper sx={{ p: 2, width: 500, mt: 2, mb: 3 }}>
            <Typography sx={{ mb: 1, fontWeight: 500 }}>
              Która z odpowiedzi jest poprawna?
            </Typography>
            <RadioGroup>
              <FormControlLabel value="1" control={<Radio></Radio>} label="1" />
              <FormControlLabel value="2" control={<Radio></Radio>} label="2" />
              <FormControlLabel value="3" control={<Radio></Radio>} label="3" />
              <FormControlLabel value="4" control={<Radio></Radio>} label="4" />
            </RadioGroup>
          </Paper>
          <Paper sx={{ p: 2, width: 500, mt: 2, mb: 3 }}>
            <Typography sx={{ mb: 2.5, fontWeight: 500 }}>
              To pytanie otwarte. Udziel odpowiedzi ponizej
            </Typography>
            <TextField
              placeholder="Twoja odpowiedź"
              variant="standard"
              multiline
              fullWidth
            ></TextField>
          </Paper>
        </>
      ) : (
        <p>Brak testu do wyświetlenia</p>
      )}
    </Box>
  );
};

export default TestPage;
