import * as React from "react";
import Box from "@mui/material/Box";
import Collapse from "@mui/material/Collapse";
import IconButton from "@mui/material/IconButton";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@mui/icons-material/KeyboardArrowUp";
import { useEffect, useState } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation } from "react-router-dom";

function createData(score, date, testname, groupname) {
  return {
    score,
    date,
    testname,
    groupname,
  };
}

function Row(props) {
  const { grades, group } = props;
  const [open, setOpen] = React.useState(false);

  return (
    <React.Fragment>
      <TableRow sx={{ "& > *": { borderBottom: "unset" } }}>
        <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell>
        <TableCell component="th" scope="row">
          {group.name}
        </TableCell>
        <TableCell align="left">test</TableCell>
        <TableCell align="left">test2</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box sx={{ margin: 1 }}>
              <Typography variant="h6" gutterBottom component="div">
                Oceny
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>Data</TableCell>
                    <TableCell>Typ</TableCell>
                    <TableCell align="right">Nazwa</TableCell>
                    <TableCell align="right">Ocena</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {grades.map((row, i) =>
                    row.groupname == group.name ? (
                      <TableRow key={"row" + i}>
                        <TableCell component="th" scope="row">
                          {row.date}
                        </TableCell>
                        <TableCell>{row.score}</TableCell>
                        <TableCell align="right">{row.testname}</TableCell>
                        <TableCell align="right">helo</TableCell>
                      </TableRow>
                    ) : (
                      <></>
                    )
                  )}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

const rows = [
  createData("Frozen yoghurt", 159, 6.0, 24, 4.0, 3.99),
  createData("Ice cream sandwich", 237, 9.0, 37, 4.3, 4.99),
  createData("Eclair", 262, 16.0, 24, 6.0, 3.79),
  createData("Cupcake", 305, 3.7, 67, 4.3, 2.5),
  createData("Gingerbread", 356, 16.0, 49, 3.9, 1.5),
];

export default function CollapsibleTable() {
  const login = window.localStorage.getItem("login");
  const [grades, setGrades] = useState([]);
  const [groups, setGroups] = useState([]);
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();

  //GETTING INFO FROM THE SERVER
  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getGrades = async () => {
      try {
        const response = await axiosPrivate.get("/grade/user/" + login, {
          signal: controller.signal,
          params: { login: login },
        });
        isMounted && setGrades(response.data.grades.rows);

        setGroups(response.data.groups.rows);
        // setGroupName(response.data.groupName.rows[0].name);

        // setRows(
        //   response.data.grades.rows.map((row) => {
        //     return createData(row);
        //   })
        // );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getGrades();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  useEffect(() => {
    console.log(grades, groups);
  }, [groups]);

  return (
    <TableContainer component={Paper}>
      <Table aria-label="collapsible table">
        <TableHead>
          <TableRow>
            <TableCell />
            <TableCell align="left">Nazwa grupy</TableCell>
            <TableCell align="left">Ilość ocen</TableCell>
            <TableCell align="left">Średnia</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {groups.map((group, i) => (
            <Row key={i} grades={grades} group={group} />
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
