import React, { useState, useEffect } from "react";
import socketio from "socket.io-client";
import Box from "@mui/material/Box";
import {
  Avatar,
  Button,
  Card,
  Divider,
  Paper,
  TextField,
  Typography,
} from "@mui/material";
import dayjs from "dayjs";

const socket = socketio("http://localhost:3000/chat/", {
  transports: ["websocket"],
});

function createData(obj) {
  //const counter = 2;
  const id = obj.id;
  const name = obj.name;
  const owner = obj.first_name + " " + obj.last_name;
  const ownerLogin = obj.login;
  const students_count = obj.students_count;
  return { id, name, owner, students_count, ownerLogin };
}

const Chat = () => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [group, setGroup] = useState();

  const login = localStorage.getItem("login");

  //GETTING INFO FROM THE SERVER
  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    const getUsersGroups = async () => {
      try {
        const response = await axiosPrivate.get("/group", {
          signal: controller.signal,
          params: { login: login },
        });
        isMounted && setGroup(response.data.rows);
        setGroup(
          response.data.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersGroups();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  useEffect(() => {
    socket.on("new message", (obj) => {
      console.log(obj);
      console.log(messages);
      setMessages([...messages, obj]);
    });
  }, [messages]);

  const handleSendMessage = () => {
    let timestamp = dayjs();
    console.log(timestamp);
    socket.auth = { login };
    let obj = {
      message: message,
      login: login,
      timestamp: timestamp,
    };
    socket.emit("send message", obj);
    setMessage("");
  };

  socket.on("connect_error", (err) => {
    if (err.message === "invalid username") {
      console.log("invalid username");
    }
  });

  socket.on("users", (users) => {
    console.log(users);
  });

  const handleKeypress = (e) => {
    if (e.keyCode === 13) {
      handleSendMessage();
    }
  };

  return (
    <Box
      sx={{
        width: "50%",
        minWidth: 375,
        height: "92vh",
        backgroundColor: "white",
        mr: "auto",
        ml: "auto",
        display: "flex",
        flexDirection: "column",
        justifyContent: "end",
        alignItems: "center",
      }}
    >
      <TableContainer component={Paper} sx={{ maxWidth: 1000, mt: 7 }}>
        <Table sx={{ Width: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align="left">ID</TableCell>
              <TableCell align="center">Nazwa</TableCell>
              <TableCell align="center">Nauczyciel</TableCell>
              <TableCell className="shortTableCell" align="center">
                Ilość studentów
              </TableCell>
              <TableCell align="center" className="shortTableCell">
                Akcje
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody className="tableBodyGroups">
            {group.map((row, i) => (
              <TableRow
                className="groupTableHover"
                key={i}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell
                  onClick={() => {
                    navigate("/groups/" + row.id);
                  }}
                  align="left"
                >
                  {i + 1}
                </TableCell>
                <TableCell
                  onClick={() => {
                    navigate("/groups/" + row.id);
                  }}
                  align="center"
                >
                  {row.name}
                </TableCell>
                <TableCell
                  onClick={() => {
                    navigate("/groups/" + row.id);
                  }}
                  align="center"
                >
                  {row.owner}
                </TableCell>
                <TableCell
                  onClick={() => {
                    navigate("/groups/" + row.id);
                  }}
                  align="center"
                  className="shortTableCell"
                >
                  {row.students_count}
                </TableCell>
                <TableCell className="shortTableCell">
                  {row.ownerLogin == login ? (
                    <>
                      <Tooltip title="Dodaj uczniów">
                        <IconButton onClick={() => handleOpenAdd(row.id)}>
                          <AddIcon fontSize="medium" color="success" />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Edytuj grupe">
                        <IconButton>
                          <EditIcon fontSize="medium" />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Usuń">
                        <IconButton>
                          <DeleteIcon fontSize="medium" color="error" />
                        </IconButton>
                      </Tooltip>
                    </>
                  ) : (
                    <></>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ width: "100%" }}>
        <Typography
          sx={{
            textAlign: "left",
            fontWeight: 500,
            color: "rgba(0, 0, 0, 0.6)",
            ml: 3,
            mt: 1,
            mb: 1,
          }}
        >
          Czat: Kamil Jupiter
        </Typography>
      </Box>
      <Divider sx={{ width: "100%", heigth: 3 }}></Divider>

      <Box sx={{ width: "100%", height: "100%", mt: 3 }}>
        <Box>
          {messages.map((m) =>
            m.login == login ? (
              <Box
                sx={{
                  display: "flex",
                  backgroundColor: "white",
                  height: "100%",
                  justifyContent: "flex-end",
                  mr: 2,
                  mt: 1,
                  mb: 1,
                }}
              >
                <Paper sx={{ ml: 2, pt: 1, pb: 1, pl: 2, pr: 2 }}>
                  <Typography>{m.message}</Typography>
                </Paper>
              </Box>
            ) : (
              <Box
                sx={{
                  display: "flex",
                  backgroundColor: "white",
                  height: "100%",
                  justifyContent: "flex-start",
                  ml: 2,
                  mt: 1,
                  mb: 1,
                }}
              >
                <Avatar sx={{ mt: "auto", mb: "auto" }} sizes="small">
                  {m.login[0]}
                </Avatar>
                <Paper sx={{ ml: 2, pt: 1, pb: 1, pl: 2, pr: 2 }}>
                  <Typography
                    sx={{ color: "rgba(0, 0, 0, 0.6)", fontSize: 10 }}
                  >
                    Kamil Jowisz {dayjs(m.timestamp).format("H:mm")}
                  </Typography>
                  <Typography>{m.message}</Typography>
                </Paper>
              </Box>
            )
          )}
        </Box>
      </Box>
      <Box
        sx={{
          width: "80%",
          mb: 3,
          display: "flex",
          flexDirection: "row",
          gap: 2,
        }}
      >
        <TextField
          autoFocus
          variant="standard"
          value={message}
          sx={{ width: "100%" }}
          onKeyDown={handleKeypress}
          onChange={(e) => setMessage(e.target.value)}
        />
        <Button variant="contained" onClick={handleSendMessage}>
          Send
        </Button>
      </Box>
    </Box>
  );
};

export default Chat;
