import { useState, useEffect } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import { Button } from "@mui/material";
import useRefreshToken from "./hooks/useRefreshToken";
import useAuth from "./hooks/useAuth";

const Profile = () => {
  const [profile, setProfile] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  //const { state } = useLocation();
  const { login } = useParams();
  const { auth } = useAuth();
  const [loginProf, setLoginProf] = useState(
    "guwno-----------------------------------------------------------------"
  );

  // if (!login) {
  //   setLoginProf(auth.login);
  // }
  console.log("---------LOGIN PROF", loginProf);

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    console.log("---------LOGIN PROF", loginProf);
    const getUsersProfile = async () => {
      try {
        const response = await axiosPrivate.get(
          "/profile",
          JSON.stringify({ loginProf }),
          {
            signal: controller.signal,
            login: loginProf,
          }
        );
        isMounted && setProfile(response.data.rows[0]);
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersProfile();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <article>
      <h2>Profile</h2>
      {profile?.length ? <p>{profile}</p> : <p>Brak profilu do wy≈õwietlenia</p>}
    </article>
  );
};

export default Profile;
