import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  RadioGroup,
  FormControlLabel,
  Radio,
  TextField,
  FormGroup,
  Checkbox,
  Collapse,
} from "@mui/material";
import { useState } from "react";
import QuestionCreator from "./QuestionCreator";
import { TransitionGroup } from "react-transition-group";

const TestCreator = () => {
  const [questionList, setQuestionList] = useState([]);
  const onAddBtnClick = (event) => {
    setQuestionList(
      questionList.concat(<QuestionCreator id={questionList.length} />)
    );
  };
  const handleRemove = (item) => {
    setQuestionList((prev) => [...prev.filter((i) => i !== item)]);
  };

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 3,
      }}
    >
      <Typography variant="h5" sx={{ fontWeight: 500, mb: 3 }}>
        Kreator Testu
      </Typography>
      <Divider sx={{ width: "100%" }}></Divider>
      <Button onClick={onAddBtnClick}>Click</Button>
      <TransitionGroup>
        {questionList.map((question) => (
          <Collapse>
            {question} <Button onClick={handleRemove(question)}>Usun</Button>
          </Collapse>
        ))}
      </TransitionGroup>
    </Box>
  );
};

export default TestCreator;
