import * as React from "react";
import { useState } from "react";
import './Stylesheets/LoginPage.css';
import { Box,Link,Paper, Button, Checkbox,FormControlLabel, TextField }from "@mui/material";
import axios from "axios"

function LoginPage(){
    //Variables describing users credentials
    const [login, setLogin] = useState('');
    const [password, setPassowrd] = useState('');

    //Variables describing alerts
    const [alert, setAlert] = useState(false);
    const [alertType, setAlertType] = useState('');
    const [alertMessage, setAlertMessage] = useState('');

    axios.interceptors.request.use(function (config) {
        const token = "BEARER " + localStorage.getItem("jwtToken");
        config.headers.Authorization =  token;

        return config;
    });

    const handleSubmit = async (event) => {
        event.preventDefault();

        console.log(login);
        console.log(password);

        await axios.post('/login',{
            login: login,
            password: password,
        }).then((response) => { 
            localStorage.setItem("jwtToken", response.data.accessToken)
            if(response.data.redirect == '/dashboard'){
                window.location.replace('/dashboard');
            }
            console.log(response);
        }).catch((error) => { //Error handling
            if(error.response){ //The client was given an error response

                if(!error.response.data.type){
                    if(error.response.status >= 400){
                        setAlertType('error');
                    }else if(error.response.status >= 200){
                        setAlertType('success');
                    }
                }else{
                    setAlertType(error.response.data.type);
                }

                setAlert(true);
                setAlertMessage(error.response.data.message);

                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
                console.log(alertMessage)
            }else if(error.request){ //The client never received a response
                console.log(error.request)
            }else{ //Other errors
                console.log('Error', error.message);
            }
        })
    };

    return (<Paper onSubmit={handleSubmit} className="LoginPaper" sx={{width:400}} elevation={3}><Box component="form" sx={{mt:5}} gap={5} display="flex" flexDirection="column" justifyContent="center">
        <h1 className="LogoHeader">Epinote</h1>
        <TextField helperText={alertType === 'Login' ? alertMessage : ""} error={alertType === 'Login' ? alertMessage : ""} onChange={event => setLogin(event.target.value)} value={login} required autofocus label="Login" name="login" id="login" sx={{mr:5, ml:5}} variant="standard" size="small"/>
        <Box sx={{mr:5, ml:5}}>
            <TextField helperText={alertType === 'Password' ? alertMessage : ""} error={alertType === 'Password' ? alertMessage : ""} onChange={event => setPassowrd(event.target.value)} value={password} required fullWidth label="Password" name="password" id="password" type="password" variant="standard" size="small"/>
            <FormControlLabel control={<Checkbox size="small"/>} label="Remember Me"/>
        </Box>

        <Box sx={{mr:5, ml:5, mb:7}} display="flex" justifyContent="space-between" gap={5}> 
            <Link variant="text" component={Button} underline="none" href='/register'>Create Account</Link>
            <Button  variant="contained" type="submit">Log In</Button>
        </Box>
        </Box></Paper>);
}

export default LoginPage;
