import {
  Card,
  TextField,
  Typography,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Divider,
  Box,
  Tabs,
  Tab,
  Grow,
} from "@mui/material";
import React from "react";
import TabPanel from "./TabPanel";

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

const QuestionCreator = () => {
  const [value, setValue] = React.useState(0);
  const [checked, setChecked] = React.useState(true);
  const handleChange = (event, newValue) => {
    setValue(newValue);
    setChecked((prev) => !prev);
  };

  return (
    <Grow in={checked} collapsedSize={300}>
      <Card
        sx={{
          p: 2,
          width: { sm: 410, lg: 550, xs: 310 },
          maxWidth: 700,
          minWidth: 200,
          minHeight: 350,
          mt: 2,
          mb: 3,
        }}
      >
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
          variant="fullWidth"
        >
          <Tab label="Pytanie otwarte" {...a11yProps(0)} />
          <Tab label="Pytanie zamkniete" {...a11yProps(1)} />
        </Tabs>
        <TextField
          fullWidth
          placeholder="Dodaj pytanie"
          label="Pytanie"
          variant="standard"
          sx={{ mb: 2 }}
        ></TextField>
        <TabPanel value={value} index={0}>
          <FormGroup>
            <FormControlLabel
              sx={{ mb: 0.5 }}
              control={<Checkbox className="creatorCheckbox" name="1" />}
              label={
                <TextField
                  className="creatorTextField"
                  label="Odpowiedź"
                  variant="standard"
                ></TextField>
              }
            />
            <FormControlLabel
              sx={{ mb: 0.5 }}
              control={<Checkbox className="creatorCheckbox" name="1" />}
              label={
                <TextField
                  className="creatorTextField"
                  label="Odpowiedź"
                  variant="standard"
                ></TextField>
              }
            />
            <FormControlLabel
              sx={{ mb: 0.5 }}
              control={<Checkbox className="creatorCheckbox" name="1" />}
              label={
                <TextField
                  className="creatorTextField"
                  label="Odpowiedź"
                  variant="standard"
                ></TextField>
              }
            />
            <FormControlLabel
              sx={{ mb: 0.5 }}
              control={<Checkbox className="creatorCheckbox" name="1" />}
              label={
                <TextField
                  className="creatorTextField"
                  label="Odpowiedź"
                  variant="standard"
                ></TextField>
              }
            />
          </FormGroup>
        </TabPanel>
        <TabPanel value={value} index={1}></TabPanel>
      </Card>
    </Grow>
  );
};

export default QuestionCreator;
