import { useState, useEffect, useStyles } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import useAuth from "./hooks/useAuth";

function createData(obj) {
  const id = obj.id;
  const name = obj.name;
  const owner = obj.first_name + " " + obj.last_name;
  const students_count = obj.students_count;
  return { id, name, owner, students_count };
}

const GroupsBoardPage = () => {
  const [group, setGroup] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const { auth } = useAuth();
  const [loginProf, setLoginProf] = useState();
  const login = window.localStorage.getItem("login");
  const groupId = window.location.pathname.replace("/groups/", "");

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    console.log(groupId);
    const getUsersGroups = async () => {
      try {
        const response = await axiosPrivate.get("/group/" + groupId, {
          signal: controller.signal,
          params: { login: login, group: groupId },
        });
        isMounted && setGroup(response.data.rows);
        setGroup(
          response.data.rows.map((row) => {
            return createData(row);
          })[0]
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersGroups();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  console.log(group);
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {group ? <p>{group.name}</p> : <p>Brak grupy do wy≈õwietlenia</p>}
    </Box>
  );
};

export default GroupsBoardPage;
