import * as React from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { useNavigate } from "react-router-dom";
import useAuth from "./hooks/useAuth";
import useLogout from "./hooks/useLogout";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import { Box } from "@mui/system";

const PopUpForm = () => {
  const [open, setOpen] = React.useState(false);
  const logout = useLogout();
  const navigate = useNavigate();

  const signOut = async () => {
    await logout();
    navigate("/login");
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    await axios
      .post("/login", JSON.stringify({ login, password }), {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      })
      .then((response) => {
        // setAccessToken(response.data.accessToken);
        // setRoles([response.data.roles]);
        // console.log(roles, accessToken);
        let roles = response.data.roles;
        console.log(roles);
        let accessToken = response.data.accessToken;
        setAuth({ login, password, roles, accessToken });
        navigate("/dashboard", true);
      })
      .catch((error) => {
        //Error handling
        if (error.response) {
          //The client was given an error response

          if (!error.response.data.type) {
            if (error.response.status >= 400) {
              setAlertType("error");
            } else if (error.response.status >= 200) {
              setAlertType("success");
            }
          } else {
            setAlertType(error.response.data.type);
          }

          setAlert(true);
          setAlertMessage(error.response.data.message);

          console.log(error.response.data);
          console.log(error.response.status);
          console.log(error.response.headers);
          console.log(alertMessage);
        } else if (error.request) {
          //The client never received a response
          console.log(error.request);
        } else {
          //Other errors
          console.log("Error", error.message);
        }
      });
  };

  return (
    <div>
      <Dialog
        onSubmit={handleSubmit}
        component="form"
        open={true}
        onClose={handleClose}
      >
        <DialogTitle>Informacje dodatkowe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Aby rozpocząć korzystanie z naszej platformy potrzebujemy kilku
            dodatkowych informacji o Tobie.
          </DialogContentText>
          <Box sx={{ display: "flex", gap: "4rem" }}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Imię"
              type="firstname"
              fullWidth
              variant="standard"
            />
            <TextField
              autoFocus
              margin="dense"
              id="name"
              type="lastname"
              label="Nazwisko"
              fullWidth
              variant="standard"
            />
          </Box>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Adres e-mail"
            type="email"
            fullWidth
            variant="standard"
            sx={{ mb: "1rem" }}
          />
          <FormControl>
            <FormLabel>Rola</FormLabel>
            <RadioGroup defaultValue="Student" row>
              <FormControlLabel
                labelPlacement="end"
                value="Student"
                control={<Radio />}
                label="Student"
              />
              <FormControlLabel
                value="Teacher"
                control={<Radio />}
                label="Nauczyciel"
              />
            </RadioGroup>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={signOut}>Wyloguj</Button>
          <Button onClick={handleClose}>Wyślij</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default PopUpForm;
