import { useState, useEffect } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation } from "react-router-dom";
import React from "react";
import TestStartPopUp from "./TestStartPopUp";
import {
  Divider,
  Typography,
  Card,
  Box,
  FormControlLabel,
  TextField,
  FormGroup,
  Checkbox,
  Button,
} from "@mui/material";

function createData(obj) {
  const description = obj.description;
  const results = [obj.answer_1, obj.answer_2, obj.answer_3, obj.answer_4];
  const array = results.filter((element) => {
    return element !== null;
  });
  return { description, array };
}

const TestPage = () => {
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const login = window.localStorage.getItem("login");
  const testId = window.location.pathname.replace("/test/solve/", "");
  const [test, setTest] = useState([]);
  const [testName, setTestName] = useState();
  const [teacher, setTeacher] = useState("");
  const [testTime, setTestTime] = useState("");
  const [startDate, setStartDate] = useState("");
  const [answerArray, setAnswerArray] = useState([]);
  const [checkedValue, setCheckedValue] = useState(false);
  const [value, setValue] = useState("");

  const answerArrayTemp = [];

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getTest = async () => {
      try {
        const response = await axiosPrivate.get("/test/solve/" + testId, {
          signal: controller.signal,
          params: { login: login, test: testId },
        });
        console.log(response);
        isMounted && setTest(response.data.test.rows);
        setTestName(response.data.testDetails.rows[0].name);
        console.log(response.data.testDetails.rows);
        setTeacher(
          response.data.testDetails.rows[0].first_name +
            " " +
            response.data.testDetails.rows[0].last_name
        );
        setTestTime(response.data.testDetails.rows[0].time);
        setStartDate(response.data.testDetails.rows[0].date);

        setTest(
          response.data.test.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getTest();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  // useEffect(() => {
  //   test.map((row, i) => {
  //     let tempArray = [];
  //     for (let h = 0; h < row.array.length; h++) {
  //       tempArray.push(false);
  //     }
  //     answerArrayTemp.push(tempArray);
  //   });
  //   setAnswerArray(answerArrayTemp);
  // }, [test]);

  useEffect(() => {
    console.log("update", answerArray);
  }, [answerArray]);

  // console.log(test);
  return (
    <Box
      component="form"
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      <TestStartPopUp
        teacher={teacher}
        testTime={testTime}
        testName={testName}
        startDate={startDate}
      ></TestStartPopUp>
      {testName ? (
        <>
          <Typography sx={{ color: "rgba(0, 0, 0, 0.6)" }} variant="h5">
            {testName}
          </Typography>
          <Divider sx={{ width: "100%", mb: 2, mt: 2 }}></Divider>

          {test.map((row, i) => (
            <Card
              sx={{
                p: 2,
                width: { sm: 410, lg: 550, xs: 310 },
                maxWidth: 700,
                minWidth: 200,
                mt: 2,
                mb: 3,
              }}
            >
              <Typography
                sx={{ mb: 2.5, fontWeight: 500, color: "rgba(0, 0, 0, 0.6)" }}
              >
                {i + 1}. {row.description}
              </Typography>
              {row.array.length != 0 ? (
                <FormGroup>
                  {row.array.map((x, j) => (
                    <FormControlLabel
                      control={
                        <Checkbox
                          value={answerArray}
                          // checked={answerArray[i] ? answerArray[i][j] : false}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setAnswerArray([
                                ...answerArray,
                                {
                                  id: i + "" + j,
                                  state: true,
                                },
                              ]);
                            } else {
                              // remove from list
                              setAnswerArray(
                                answerArray.filter(
                                  (answer) => answer.id !== i + "" + j
                                )
                              );
                            }
                          }}
                          name={j}
                        />
                      }
                      label={x}
                      sx={{ color: "rgba(0, 0, 0, 0.6)" }}
                    />
                  ))}
                </FormGroup>
              ) : (
                <TextField
                  placeholder="Twoja odpowiedź"
                  variant="standard"
                  multiline
                  fullWidth
                  value={
                    answerArray.filter((obj) => {
                      return obj.id === i;
                    }).text
                  }
                  onChange={(e) => {
                    setAnswerArray([
                      ...answerArray,
                      {
                        id: i,
                        text: e.target.value,
                      },
                    ]);
                  }}
                  sx={{ color: "rgba(0, 0, 0, 0.6)" }}
                ></TextField>
              )}
            </Card>
          ))}
        </>
      ) : (
        <p>Brak testu do wyświetlenia</p>
      )}
      <Button>Wyślij test</Button>
    </Box>
  );
};

export default TestPage;
