import { useState, useEffect } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import useAuth from "./hooks/useAuth";

function createData(id, name, owner, students_num) {
  return { id, name, owner, students_num };
}

const rows = [
  createData(1, "Frozen yoghurt", 159, 6.0),
  createData(2, "Ice cream sandwich", 237, 9.0),
  createData(3, "Eclair", 262, 16.0),
  createData(4, "Cupcake", 305, 3.7),
  createData(5, "Gingerbread", 356, 16.0),
];

const styles = (theme) => ({
  tr: {
    background: "black !important",
    "&:hover": {
      background: "blue !important",
    },
  },
});

const GroupsBoardPage = () => {
  const [group, setGroup] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  //const { state } = useLocation();
  const { login } = useParams();
  const { auth } = useAuth();
  const [loginProf, setLoginProf] = useState();

  if (!login) {
    setLoginProf(auth.login);
    //setLoginProf("flafyx");
  }

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    const getUsersGroups = async () => {
      try {
        const response = await axiosPrivate.get("/group", {
          signal: controller.signal,
          params: { login: login },
        });
        isMounted && setGroup(response.data.rows[0]);
        console.log("response", response.data.rows[0]);
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersGroups();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {group ? (
        <TableContainer component={Paper} sx={{ maxWidth: 1000 }}>
          <Table sx={{ Width: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="left">ID</TableCell>
                <TableCell align="center">Nazwa</TableCell>
                <TableCell align="center">Nauczyciel</TableCell>
                <TableCell align="center">Ilość studentów</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (
                <TableRow
                  className={styles.tr}
                  onClick={() => {
                    navigate("/dashboard");
                  }}
                  key={row.id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell align="left">{row.id}</TableCell>
                  <TableCell align="center">{row.name}</TableCell>
                  <TableCell align="center">{row.owner}</TableCell>
                  <TableCell align="center">{row.students_num}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      ) : (
        <p>Brak grupy do wyświetlenia</p>
      )}
    </Box>
  );
};

export default GroupsBoardPage;
