import { useState, useEffect, useStyles } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import {
  Button,
  Divider,
  Typography,
  Card,
  Box,
  AvatarTable,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  RadioGroup,
  FormControlLabel,
  Radio,
  TextField,
  FormGroup,
  Checkbox,
} from "@mui/material";
import useAuth from "./hooks/useAuth";

function createData(obj) {
  const description = obj.description;
  const array = [obj.answer_1, obj.answer_2, obj.answer_3, obj.answer_4];
  return { description, array };
}

function allAreNull(arr) {
  return arr.every((element) => element === null);
}

const TestPage = () => {
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  const login = window.localStorage.getItem("login");
  const testId = window.location.pathname.replace("/test/", "");
  const [test, setTest] = useState();
  const [testName, setTestName] = useState();

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();
    const getTest = async () => {
      try {
        const response = await axiosPrivate.get("/test/" + testId, {
          signal: controller.signal,
          params: { login: login, test: testId },
        });
        console.log(response);
        isMounted && setTest(response.data.test.rows);
        setTestName(response.data.testName.rows[0].name);

        setTest(
          response.data.test.rows.map((row) => {
            return createData(row);
          })
        );
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getTest();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  console.log(test);
  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {testName ? (
        <>
          <Typography variant="h5">{testName}</Typography>
          <Divider sx={{ width: "100%", mb: 2, mt: 2 }}></Divider>

          {test.map((row, i) => (
            <Paper sx={{ p: 2, width: 500, mt: 2, mb: 3 }}>
              <Typography sx={{ mb: 2.5, fontWeight: 500 }}>
                {i + 1}. {row.description}
              </Typography>
              {!allAreNull(row.array) ? (
                <FormGroup>
                  {row.array.map((x) => (
                    <FormControlLabel
                      control={<Checkbox name="1" />}
                      label={x}
                    />
                  ))}
                </FormGroup>
              ) : (
                <TextField
                  placeholder="Twoja odpowiedź"
                  variant="standard"
                  multiline
                  fullWidth
                ></TextField>
              )}
            </Paper>
          ))}
        </>
      ) : (
        <p>Brak testu do wyświetlenia</p>
      )}
    </Box>
  );
};

export default TestPage;
