import { useState, useEffect } from "react";
import useAxiosPrivate from "./hooks/useAxiosPrivate";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import React from "react";
import { Button, Divider, Typography, Card, Box, Avatar } from "@mui/material";
import useAuth from "./hooks/useAuth";

const GroupsBoardPage = () => {
  const [group, setGroup] = useState();
  const axiosPrivate = useAxiosPrivate();
  const navigate = useNavigate();
  const location = useLocation();
  //const { state } = useLocation();
  const { login } = useParams();
  const { auth } = useAuth();
  const [loginProf, setLoginProf] = useState();

  if (!login) {
    setLoginProf(auth.login);
    //setLoginProf("flafyx");
  }

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    const getUsersGroups = async () => {
      try {
        const response = await axiosPrivate.get("/group", {
          signal: controller.signal,
          params: { login: login },
        });
        isMounted && setGroup(response.data.rows);
        console.log("response", response.data.rows);
      } catch (err) {
        console.error(err);
        navigate("/login", { state: { from: location }, replace: true });
      }
    };

    getUsersGroups();

    return () => {
      isMounted = false;
      controller.abort();
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        height: "100%",
        mt: 7,
      }}
    >
      {group ? (
        <Card
          sx={{
            width: 350,
            height: 500,
            m: 2,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: 3,
          }}
        >
          <Avatar sx={{ width: 56, height: 56, mt: 3, bgcolor: "#8fa5f2" }}>
            {group.name[0].toUpperCase()}
          </Avatar>
          {/*<Box sx={{ display: "flex", gap: 1 }}>
            <Typography sx={{ fontWeight: 500 }}>
              {group.first_name[0].toUpperCase() +
                group.first_name.substring(1)}

              {" " +
                group.last_name[0].toUpperCase() +
                group.last_name.substring(1)}
            </Typography>
              </Box>*/}
          <Divider sx={{ width: "100%" }}></Divider>
          <Box sx={{ display: "flex", gap: 1, width: "70%" }}>
            <Typography sx={{ textAlign: "start", fontWeight: 500 }}>
              Nazwa:{" "}
            </Typography>
            <Typography>{group.name}</Typography>
          </Box>
          <Box sx={{ mt: "auto", mb: 5 }}>
            <Button>Wyślij mail</Button>
            <Button>Napisz wiadomość</Button>
          </Box>
        </Card>
      ) : (
        <p>Brak grupy do wyświetlenia</p>
      )}
    </Box>
  );
};

export default GroupsBoardPage;
